// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.16;

interface ICustomERC721 {
    function transferFrom(address from, address to, uint256 id) external;
}

contract Escrow {
    address public nftAddress;
    address payable public seller;
    address public inspector;
    address public lender;

    mapping(uint256 => bool) public isListed;
    mapping(uint256 => uint256) public purchasePrice;
    mapping(uint256 => uint256) public escrowAmount;
    mapping(uint256 => address) public buyer;
    mapping(uint256 => bool) public inspectionPassed;
    mapping(uint256 => mapping(address => bool)) public approval;

    modifier onlyBuyer(uint256 nftID) {
        require(msg.sender == buyer[nftID], "Only buyer allowed");
        _;
    }

    modifier onlySeller() {
        require(msg.sender == seller, "Only seller allowed");
        _;
    }

    modifier onlyInspector() {
        require(msg.sender == inspector, "Only inspector allowed");
        _;
    }

    constructor(
        address _nftAddress,
        address payable _seller,
        address _inspector,
        address _lender
    ) {
        nftAddress = _nftAddress;
        seller = _seller;
        inspector = _inspector;
        lender = _lender;
    }

    function list(
        uint256 nftID,
        address buyerAddress,
        uint256 _purchasePrice,
        uint256 _escrowAmount
    ) external payable onlySeller {
        ICustomERC721(nftAddress).transferFrom(msg.sender, address(this), nftID);

        isListed[nftID] = true;
        purchasePrice[nftID] = _purchasePrice;
        escrowAmount[nftID] = _escrowAmount;
        buyer[nftID] = buyerAddress;
    }

    function depositEarnest(uint256 nftID) external payable onlyBuyer(nftID) {
        require(msg.value >= escrowAmount[nftID], "Insufficient escrow amount");
    }

    function updateInspectionStatus(uint256 nftID, bool passed) external onlyInspector {
        inspectionPassed[nftID] = passed;
    }

    function approveSale(uint256 nftID) external {
        approval[nftID][msg.sender] = true;
    }

    function finalizeSale(uint256 nftID) external {
        require(inspectionPassed[nftID], "Inspection not passed");
        require(approval[nftID][buyer[nftID]], "Buyer not approved");
        require(approval[nftID][seller], "Seller not approved");
        require(approval[nftID][lender], "Lender not approved");
        require(address(this).balance >= purchasePrice[nftID], "Insufficient funds");

        isListed[nftID] = false;

        (bool success, ) = seller.call{value: address(this).balance}("");
        require(success, "Transfer failed");

        ICustomERC721(nftAddress).transferFrom(address(this), buyer[nftID], nftID);
    }

    function cancelSale(uint256 nftID) external {
        if (!inspectionPassed[nftID]) {
            payable(buyer[nftID]).transfer(address(this).balance);
        } else {
            seller.transfer(address(this).balance);
        }
    }

    receive() external payable {}

    function getBalance() external view returns (uint256) {
        return address(this).balance;
    }
}
